export default {
  methods: {
    // 显示
    showTitle (item) {
      return item.title
    },
    // 是否显示子菜单
    showChildren (item) {
      return item.children && (item.children.length > 1 || (item.meta && item.meta.showAlways))
    },
    // 设置name类别, children0传入是否点击当前菜单跳转当前菜单第一个子目录
    getNameOrHref (item, children0) {
      return item.href ? `href-${item.href}` : ('name-' + (children0 && item.children.length > 0 ? item.children[0].name : item.name))
    }
  }
}