// 子菜单
<template>
  <Submenu :name="`${parentName}`">
    <template slot="title">
      <Icon :type="parentItem.icon || ''"/>
      <span>{{ showTitle(parentItem) }}</span>
    </template>
    <template v-for="item in children">
      <template v-if="item.children && item.children.length === 1">
        <RMenuItem v-if="showChildren(item)" :key="`menu-${item.name}`" :parent-item="item"></RMenuItem>
        <menu-item v-else :name="getNameOrHref(item, true)" :key="`menu-${item.children[0].name}`">
          <Icon :type="item.children[0].icon || ''"/><span>{{ showTitle(item.children[0]) }}</span>
        </menu-item>
      </template>
      <template v-else>
        <RMenuItem v-if="showChildren(item)" :key="`menu-${item.name}`" :parent-item="item"></RMenuItem>
        <menu-item v-else :name="getNameOrHref(item)" :key="`menu-${item.name}`"><Icon :type="item.icon || ''"/><span>{{ showTitle(item) }}</span></menu-item>
      </template>
    </template>
  </Submenu>
</template>

<script>
import itemMixin from './item-mixin'
export default {
  mixins: [itemMixin],
  name: 'RMenuItem',
  props: {
    parentItem: {
      type: Object,
      default: () => {}
    } // 父菜单
  },
  computed: {
    parentName () {
      return this.parentItem.name
    },
    children () {
      return this.parentItem.children
    }
  },
  methods: {
  }
}
</script>

