// 菜单封装
<template>
  <div class="r-menu">
    <slot></slot>
    <Menu v-bind="$props" ref="menu" width="auto" @on-select="handleSelect">
      <template v-for="item in menus">
        <template v-if="item.children && item.children.length === 1">
          <RMenuItem v-if="showChildren(item)" :key="`menu-${item.name}`" :parent-item="item"></RMenuItem>
          <menu-item v-else :name="getNameOrHref(item, true)" :key="`menu-${item.children[0].name}`">
            <Icon :type="item.children[0].icon || ''"/><span>
            {{ showTitle(item.children[0]) }}</span></menu-item>
        </template>
        <template v-else>
          <RMenuItem v-if="showChildren(item)" :key="`menu-${item.name}`" :parent-item="item"></RMenuItem>
          <menu-item v-else :name="getNameOrHref(item)" :key="`menu-${item.name}`">
            <Icon :type="item.icon || ''"/><span>{{ showTitle(item) }}</span></menu-item>
        </template>
      </template>
    </Menu>
  </div>
</template>

<script>
import {Menu} from 'iview'
import itemMixin from './item-mixin'
import RMenuItem from './r-menu-item.vue'
export default {
  name: 'RMenu',
  components: {
    RMenuItem
  },
  mixins: [itemMixin],
  props: {
    ...Menu.props,
    menus: {
      type: Array,
      default () {
        return []
      }
    }, // 菜单列表
    collapsed: {
      type: Boolean
    }, // 展开收起
    theme: {
      type: String,
      default: 'light'
    }, // 样式主题
    rootIconSize: {
      type: Number,
      default: 20
    }, 
    iconSize: {
      type: Number,
      default: 16
    },
    accordion: Boolean, // 是否至多展开一个子菜单
    activeName: {
      type: String,
      default: ''
    }, // 激活菜单的name
    openNames: {
      type: Array,
      default: () => []
    } // 已展开的 Submenu 的 name 集合
  },
  data () {
    return {
      openedNames: []
    }
  },
  methods: {
    // 选择某个菜单
    handleSelect (name) {
      this.$emit('on-select', name)
    },

  }
}
</script>

